import React,{useEffect,useState} from 'react'
import DialogTitle from '@mui/material/DialogTitle';
import Dialog from '@mui/material/Dialog';
import axios from "axios";
import {Button} from "@mui/material";
import {DialogActions, TextField} from "@mui/material";
const NewTodoList = props => {
    const [stateDialog,updateState]=useState(props.openDialog)
    const [title,updateTitle]=useState('')
    useEffect(()=>{
        updateState(props.openDialog)
    },[])
    const handleClose = () => {
        updateState(false)
        console.log('clicked',stateDialog)
    };
    const addNewList = () => {
        var data = {};
        data.title = title;
        axios.post('todo-new-list',data).then(response => {
                console.log(response);
            })
    }
    return(
        <Dialog onClose={handleClose} open={stateDialog}>
            <DialogTitle>New TodoList</DialogTitle>
            <TextField onChange={e=>updateTitle(e)} label="Todo List Title" placeholder="Type title name"></TextField>
            <DialogActions>
                <Button onClick={()=>handleClose()}>Cancel</Button>
                <Button onClick={()=>addNewList()} disabled={title.length >! 3}> Add </Button>
            </DialogActions>
        </Dialog>
    )
}
export default NewTodoList
