    }
    const deleteList=()=>{

    }
    const [stateDialog,updateState]=useState(props.openDialog)
    const [name,updateName]=useState('')
    const [info,updateInfo]=useState('')
    useEffect(()=>{
        updateState(props.openDialog)
    },[])
    const handleClose = () => {
        updateState(false)
    };
    const addNewList = () => {
        const newItem = {"todoID":props.todoID, "name": name , "info":info};
        axios.post('/todo-new-todo', newItem).then(response => alert(JSON.stringify(newItem)));
    }
    return(
            <Card style={{padding:"27px !important"}}>
                <Dialog  width="300" onClose={handleClose} open={stateDialog}>

                <DialogTitle  style={{margin:"auto"}}><Error style={{fontSize: "46px",
                    color:"#d32f2f"}}/></DialogTitle>
                <h4 style={{color:"#d32f2f"}}>Are you sure you want to delete this item ?</h4>
                <DialogActions>
                    <Button onClick={()=>handleClose()} color="primary">Cancel</Button>
                    <Button onClick={()=>addNewList()} color="error"> Delete </Button>
                </DialogActions>
                </Dialog>

            </Card>

    )
}
export default DeleteComponent
