import React,{useState,useEffect} from 'react';
import {Button, Card, ListItem} from "@mui/material";
import {Grid} from "@mui/material";
import ListSubheader from '@mui/material/ListSubheader';
import List from '@mui/material/List';
import {Tooltip} from "@mui/material";
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import {AddBox} from "@mui/icons-material";
import {Delete} from "@mui/icons-material";
import {CheckCircle} from "@mui/icons-material";
import {CheckBoxSharp} from "@mui/icons-material";
import {Divider} from "@mui/material";
import {Info} from "@mui/icons-material";
import {Close} from "@mui/icons-material";
import axios from "axios";
import NewTodoList from "./NewTodoList";
import NewItem from "./NewItem";
import DeleteComponent from "./deleteComponent";
import './Main.css'
const Main = () => {
    const [openNewTodo,updateOpenNewTodo]=useState(false)
    const [openNewItem,updateOpenNewItem]=useState(false)
    const [deleteItem,updateDeleteItem]=useState(false)
    const [deleteList,updateDeleteList]=useState(false)
    const [selectedItem,updateSelectedItem]=useState(null)
    const [selectedList,updateSelectedList]=useState(null)

    const [todoLists,updateTodoLists]=useState([])
    useEffect(()=>{
       axios.get('/todo-list').then(res=>{
           updateTodoLists(res.data.lists)
       })
    },[])
    const changeStatus=(todo,status,list)=>{
        let statusItem=status
        statusItem === 'notFinished' ? statusItem = 'Finished' : statusItem ='notFinished'
        const itemModified = {"todoID":todo, "status":statusItem,list:list};
        axios.post('/todo-status', itemModified).then(response => alert(JSON.stringify(itemModified)));
    }
    const handleData=()=>{

    }
    return(
        <Card className="mainCard">
            <h3>Todo Lists</h3>
            <Button onClick={()=>updateOpenNewTodo(true)} color="success">Add New TodoList</Button>
            {openNewTodo ? <NewTodoList openDialog={openNewTodo}/> : null}
            <Divider style={{marginBottom:"12px"}}/>
            <Grid container spacing={6}>
                {
                    todoLists.map((item,index)=>{
                        return   <Grid item xs={12} md={3} key={item.id}>
                            <Card>
                                {openNewItem ? <NewItem todoID={selectedItem}  openDialog={openNewItem}/> : null}
                                {deleteItem ? <DeleteComponent  deletingInfo="deleteItem" listID={selectedList} todoID={selectedItem}
                                                               openDialog={deleteItem}/> : null}
                                {deleteList ? <DeleteComponent deletingInfo="deleteList" listID={selectedList} todoID={selectedItem}
                                                               openDialog={deleteList}/> : null}
                                <List
                                    sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}
                                    component="nav"
                                    aria-labelledby="nested-list-subheader"
                                    subheader={
                                        <div style={{display:"flex",background: "#a7ffeb"}}>
                                            <ListSubheader style={{background: "#a7ffeb"}}   id="nested-list-subheader">
                                                {item.Title}
                                            </ListSubheader>

                                            <div>
                                                <Button ><AddBox onClick={()=>(updateOpenNewItem(true)
                                                    ,updateSelectedItem(item.id))} /></Button>
                                                <Button><Delete onClick={()=>updateDeleteList(true)} color="error"/>
                                                </Button>
                                            </div>
                                        </div>
                                    }>
                                    {item.todos.map((todo) =>
                                        <ListItem  style={todo.status=== 'Finished' ? {
                                            textDecorationLine: 'line-through',
                                            textDecorationStyle: 'solid'
                                        } : null} key={todo.id}>
                                            {todo.status !== 'notFinished' ?  <ListItemIcon>
                                                <CheckCircle style={{color:'#2e7d32'}} />
                                            </ListItemIcon> : null}
                                            <ListItemText>{todo.name}</ListItemText>
                                            <Tooltip title={todo.info}>
                                                <Button width="12px" dense="true" size="small" color="primary">
                                                    <Info/>
                                                </Button>
                                            </Tooltip>
                                            <Button onClick={()=>(updateDeleteItem(true),
                                                updateSelectedItem(todo.id))} color="error">
                                                <Delete/>
                                            </Button>
                                            <Button onClick={()=>changeStatus(todo.id,todo.status,item.id)}
                                                    color={todo.status === 'notFinished' ? "success": "warning"}>
                                                {
                                                    todo.status === 'notFinished' ? <CheckBoxSharp/>: <Close/>
                                                }
                                            </Button>
                                        </ListItem>
                                    )}

                                </List>
                            </Card>
                        </Grid>
                    })
                }
            </Grid>

        </Card>
    )
}
export default Main
